import React from 'react';
import { Book } from '../models/Book';
import '../styles/BookForm.css';  // Import the CSS file

interface BookFormProps {
    initialBook?: Book;
    onSubmit: (book: Book) => void;
    className?: string; 
}

const BookForm: React.FC<BookFormProps> = ({ initialBook, onSubmit, className }) => {
    const [book, setBook] = React.useState<Book>(initialBook || { id: 0, title: '', author: '', description: '' });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setBook({ ...book, [name]: value });
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        onSubmit(book);
    };

    return (
        <form onSubmit={handleSubmit} className={`bookForm ${className}`}>
            <div>
                <label htmlFor="title">Title</label>
                <input
                    type="text"
                    id="title"
                    name="title"
                    value={book.title}
                    onChange={handleChange}
                />
            </div>
            <div>
                <label htmlFor="author">Author</label>
                <input
                    type="text"
                    id="author"
                    name="author"
                    value={book.author}
                    onChange={handleChange}
                />
            </div>
            <div>
                <label htmlFor="description">Description</label>
                <textarea
                    id="description"
                    name="description"
                    value={book.description}
                    onChange={handleChange}
                />
            </div>
            <button type="submit">Save</button>
        </form>
    );
};

export default BookForm;
