import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import BookForm from '../components/BookForm';
import { Book } from '../models/Book';
import { getBooks, updateBook } from '../services/BookService';

const EditBookPage: React.FC = () => {
    const { id } = useParams<{ id: string }>();  // Get the book ID from the URL
    const navigate = useNavigate();
    const [book, setBook] = useState<Book | null>(null);

    useEffect(() => {
        const fetchBook = async () => {
            const books = await getBooks();  // Fetch all books
            const selectedBook = books.find((b) => b.id === parseInt(id ?? ''));  // Find the book by ID
            setBook(selectedBook || null);  // Set the selected book
        };

        fetchBook();
    }, [id]);

    const handleUpdateBook = async (updatedBook: Book) => {
        await updateBook(updatedBook);  // Update the book in the database
        navigate('/');  // Redirect to the home page
    };

    if (!book) {
        return <div>Loading...</div>;  // Show loading state if the book data is not yet loaded
    }

    return (
        <div>
            <h1>Edit Book</h1>
            {/* Render BookForm with pre-filled data and onSubmit handler */}
            <BookForm initialBook={book} onSubmit={handleUpdateBook} />
        </div>
    );
};

export default EditBookPage;
